int maxSumSubarray(vector<int>& arr, int k) {
    if (arr.size() < k) return 0;

    int windowSum = 0;
    // Calculate first window sum
    for (int i = 0; i < k; i++) {
        windowSum += arr[i];
    }

    int maxSum = windowSum;

    for (int i = k; i < arr.size(); i++) {
        windowSum = windowSum - arr[i - k] + arr[_____];
        maxSum = max(maxSum, windowSum);
    }

    return maxSum;
}

