#include <vector>
#include <unordered_map>
using namespace std;

class Solution {
public:
    int subarraysWithKDistinct(vector<int>& A, int K) {
        return atMostK(A, K) - atMostK(A, K - 1);
    }

private:
    int atMostK(vector<int>& A, int K) {
        if (K == 0) return 0;

        int left = 0;
        unordered_map<int, int> counter;
        int result = 0;

        for (int right = 0; right < A.size(); right++) {
            counter[A[right]]++;

            while (counter.size() > K) {
                counter[A[left]]--;
                if (counter[A[left]] == 0) {
                    counter.erase(A[left]);
                }
                left++;
            }

            result += right - left + 1;
        }

        return result;
    }
};

int subarraysWithKDistinct(vector<int>& A, int K) {
    Solution sol;
    return sol.atMostK(A, K) - sol.atMostK(A, K - _____);
}
